import com.mashape.unirest.http.;
import java.io.;

public class main {
	public static void main(String []args) throws Exception {
		Unirest.setTimeouts(0, 0);
		HttpResponse<String> response = Unirest.post("https://sample.padocs.com/admin/accounts/user/create")
											   .header("Accept", "application/json")
											   .header("Content-Type", "application/x-www-form-urlencoded")
											   .header("Authorization", "4B2BB00E-595A-4973-8D60-61B0F59B289")
											   .header("X-Requested-With", "5C349D9C-1355-4115-A7E2-33985860B41A")
											   .field("type", "1")
											   .field("uname", "amanda")
											   .field("password", "12345")
											   .field("fname", "Amanda")
											   .field("lname", "Jones")
											   .field("sex", "Female")
											   .field("dob", "December 20, 2020")
											   .field("mobile", "+919876543210")
											   .field("email", "amanda@demo.indiosco.com")
											   .field("status", "1")
											   .asString();

		System.out.println(response.getBody());
	}
}

